import React, {Component} from 'react'
import {Link} from 'react-router-dom'


class TopHeader extends Component {
    constructor(props){
        super(props)

        this.state = {
            auth: false,
        }
    }
componentDidMount() {
    if (localStorage.getItem('user')){
        this.setState({
            auth:true,
        })
    }
}

    render() {
        let data = JSON.parse(localStorage.getItem('user'))
        return(
            <div className='top-header'>

                <div className='container'>
                    <div className='user pull-right'>
                        <ul>
                            {!this.state.auth &&
                            <li>
                                <Link to='/admin'>
                                    <i className="fa fa-user-o"></i> вход / регистрация
                                </Link>
                            </li>
                            }
                            {this.state.auth &&
                            <li>
                                <Link to='/admin'>
                                    <i className="fa fa-user-o"></i> Войдите в личный кабинет, {data.name}
                                </Link>
                            </li>
                            }

                        </ul>
                    </div>
                </div>
            </div>
        )
    }
}

export default TopHeader
