import React from 'react'
import {connect} from "react-redux";
import {Link} from 'react-router-dom'

import {
    getTotalWishlistCount,
} from 'selectors'

const Wishlist = ({totalWishlistCount}) => {
    return (
        <Link to='/wishlist'>
            <i className="fa fa-heart-o"></i>
            <span>Избранное</span>
            <div className="qty">{totalWishlistCount}</div>
        </Link>
    )
}

const mapStateToProps = state => {
    return {
        totalWishlistCount: getTotalWishlistCount(state),
    }
}
export default connect(mapStateToProps, null)(Wishlist)