import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {fetchPhoneById, addPhoneToBasket, addPhoneToWishlist} from 'actions'
import {getPhoneById} from 'selectors'
import Header from 'components/header'
import Subscribe from 'components/subscribe'
import Footer from 'components/footer'
import TopHeader from "components/header/top-header";

class Phone extends Component {
    componentDidMount() {
        const phoneId = this.props.match.params.id
        this.props.fetchPhoneById(phoneId)
    }

    renderField() {

        const {phone} = this.props
        const columnField = R.compose(
            R.pick([
                'specifications'
            ])
        )(phone)
        const a = JSON.parse(columnField.specifications)
        const b = R.compose(
            R.toPairs,
        )(a)
        console.log(phone)
        console.log( a)
        console.log( b)

        return b.map(([key, value]) => (
            <div className='column' key={key}>
                {console.log(key, value)}
                <div className='ab-details-title'>
                    <p>{`${key}:`}</p>
                </div>
                <div className='ab-details-info'>
                    <p className='value' id='value'>{value}</p>
                </div>
            </div>
        ))
    }

    renderContent() {
        const {phone} = this.props

        return (
            <div className='phone-info thumbnail'>
                <div className='row'>
                    <div className='col-md-6'>
                        <img
                            src={phone.image}
                            alt={phone.name}
                        />
                    </div>
                    <div className='col-md-6'>
                        {this.renderField()}
                    </div>
                </div>
                <div className='caption-full'>
                    <h4>{phone.name}</h4>
                    <p>{phone.description}</p>
                    <h4 className='pull'>Цена: {phone.price} <i className="fa fa-rub" aria-hidden="true"></i></h4>

                </div>
            </div>
        )
    }

    renderSidebar() {
        const {phone, addPhoneToBasket, addPhoneToWishlist} = this.props
        return (
            <div>
                <div className='form-group'>
                    <h1>{phone.name}</h1>
                    <h2>Цена: {phone.price} <i className="fa fa-rub" aria-hidden="true"></i></h2>
                </div>
                <Link to='/' className='btn btn-info btn-block'>
                    Вернуться в магазин
                </Link>
                <button
                    type='button'
                    className='btn btn-success btn-block'
                    onClick={() => addPhoneToBasket(phone.id)}
                >
                    Добавить в корзину
                </button>
                <button
                    type='button'
                    className='btn btn-success btn-block'
                    onClick={() => addPhoneToWishlist(phone.id)}
                >
                    Добавить в избранное
                </button>
            </div>
        )
    }

    render() {
        return (
            <div className='view-container'>
                <TopHeader/>
                <Header/>
                <div className='container'>
                    <div className='row'>
                        <div className='col-md-9'>
                            {this.props.phone && this.renderContent()}
                        </div>
                        <div className='col-md-3'>
                            {this.props.phone && this.renderSidebar()}
                        </div>
                    </div>
                </div>
                <Subscribe/>
                <Footer/>
                {}
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        phone: getPhoneById(state, state.phonePage.id)
    }
}

const mapDispatchToProps = {
    fetchPhoneById,
    addPhoneToBasket,
    addPhoneToWishlist

}

export default connect(mapStateToProps, mapDispatchToProps)(Phone)