import React from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {
    removePhoneFromWishlist,
    cleanWishlist
} from 'actions'

import {
    getWishlistPhonesWithCount
} from 'selectors'

import Header from 'components/header'
import Subscribe from 'components/subscribe'
import Footer from 'components/footer'
import TopHeader from "components/header/top-header";

const Wishlist = ({
  phones,
  removePhoneFromWishlist,
  cleanWishlist
                  }) => {
    const isBasketEmpty = R.isEmpty(phones)
    const renderContentForPhones = () => {
        return (

            <div className='backet'>
                {isBasketEmpty && <div className='list-empty'>Ваш список избранного пуст</div>}

                <div className='table-responsive'>
                    <table className='table-bordered table-striped table-condensed cf'>
                        <tbody>
                        {phones.map((phone, index) => (
                            <tr
                                key={index}
                                className='item-checout'
                            >
                                <td className='first-column-checkout'>
                                    <img
                                        className='img-thumbnail'
                                        src={phone.image}
                                        alt={phone.name}
                                    />
                                </td>

                                <td>{phone.name}</td>


                                <td>{phone.price} <i className="fa fa-rub" aria-hidden="true"></i></td>
                                <td><Link to={`/phones/${phone.id}`}>Просмотреть товар</Link></td>
                                <td>
                  <span
                      onClick={() => removePhoneFromWishlist(phone.id)}
                      className='delete-cart'
                  ></span>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
                {
                    R.not(isBasketEmpty) &&
                    <div className='row'>
                        <div className='pull-right total-user-checkout'>


                        </div>
                    </div>
                }
            </div>
        )
    }

    const renderSidebar = () => (
        <div className='backet'>
            <Link
                className='btn btn-info btn-block'
                to='/'
            >
                <span className='glyphicon glyphicon-info-sign'></span>
                <span>Вернуться в магазин</span>
            </Link>
            {
                R.not(isBasketEmpty) &&
                <div>
                    <button
                        onClick={cleanWishlist}
                        className='btn btn-block btn-red btn-mr'
                    >
                        <span className='glyphicon glyphicon-trash'></span>
                        Очистить список избранного
                    </button>

                </div>
            }
        </div>
    )

    return (
        <div className='view-container'>
            <TopHeader/>
            <Header/>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-9'>
                        {renderContentForPhones()}
                    </div>
                    <div className='col-md-3'>
                        {renderSidebar()}
                    </div>
                </div>
            </div>
            <Subscribe/>
            <Footer/>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        phones: getWishlistPhonesWithCount(state),
    }
}

const mapDispatchToProps = {
    removePhoneFromWishlist,
    cleanWishlist
}

export default connect(mapStateToProps, mapDispatchToProps)(Wishlist)