import * as R from 'ramda'

import {FETCH_GROUPS_SUCCESS} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_GROUPS_SUCCESS:
            const newValues2 = R.indexBy(R.prop('name'), payload)
            return R.merge(state, newValues2)
        default:
            return state
    }
}