import * as R from 'ramda'

import {
    ADD_PHONE_TO_WISHLIST,
    REMOVE_PHONE_FROM_WISHLIST,
    CLEAN_WISHLIST,
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_PHONE_TO_WISHLIST:
            return R.uniq(R.append(payload, state))
        case REMOVE_PHONE_FROM_WISHLIST:
            return R.without(R.of(payload), state)
        case CLEAN_WISHLIST:
            return []
        default:
            return state
    }

}