import React, { useState } from 'react'
import {Switch, Route} from 'react-router'

import Phones from 'containers/phones'
import Phone from 'containers/phone'
import Basket from 'containers/basket'
import Wishlist from 'containers/wishlist'
import Dilivery from 'containers/delivery'
import About from 'containers/about'
import Repair from 'containers/repair'
import Admin from 'containers/admin'
import Login from 'containers/login'
import Signup from 'containers/signup'
import {AuthContext} from 'components/User/context'
import PrivateRoute from 'components/User/privateRouter'
import ErrorPage from 'containers/404'

function App() {
    const existingTokens = JSON.parse(localStorage.getItem("tokens"));
    const [authTokens, setAuthTokens] = useState(existingTokens);
    const setTokens = (data) => {
        localStorage.setItem("tokens", JSON.stringify(data));
        setAuthTokens(data);
    }
    return (
        <AuthContext.Provider value={{ authTokens, setAuthTokens: setTokens }}>
            <Switch>
                <Route path='/' component={Phones} exact/>
                <Route path='/basket' component={Basket}  />
                <Route path='/wishlist' component={Wishlist}  />
                <Route path='/categories/:id' component={Phones} exact/>
                <Route path='/group/:id' component={Phones} exact/>
                <Route path='/phones/:id' component={Phone} exact />
                <Route path='/delivery' component={Dilivery} exact />
                <Route path='/about' component={About} exact />
                <Route path='/repair' component={Repair} exact />
                <Route path="/login" component={Login} exact/>
                <Route path="/signup" component={Signup} exact/>
                <PrivateRoute path="/admin" component={Admin} exact/>
                <Route component={ErrorPage} exact/>
            </Switch>
        </AuthContext.Provider>
    )

}
export default App

